const data = $input.all();
const now = new Date();

// Keep only items from the last 24 hours
const last24HoursData = data.filter(item => {
  const itemDate = new Date(item.json.pubDate); // match your data path
  const diffMs = now - itemDate;
  const diffHours = diffMs / (1000 * 60 * 60);
  return diffHours <= 24; // only keep items within 24 hours
});

return last24HoursData;

