parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "title": {
      "type": ["string","null"],
      "description": "Job title extracted from the job posting (e.g., 'Full Stack Developer'). Null if not mentioned."
    },
    "company": {
      "type": ["string","null"],
      "description": "Company name from the job post. Null if not mentioned."
    },
    "recruiter_name": {
      "type": ["string","null"],
      "description": "Name of the recruiter or contact person. Null if not mentioned."
    },
    "recruiter_email": {
      "type": ["string","null"],
      "description": "Email of the recruiter/contact. Null if not mentioned."
    },
    "location": {
      "type": ["string","null"],
      "description": "Job location (city, area, or remote). Null if not mentioned."
    },
    "work_type": {
      "type": ["string","null"],
      "description": "Work type: 'Onsite', 'Remote', or 'Hybrid'. Null if not mentioned."
    },
    "experience": {
      "type": ["string","null"],
      "description": "Required experience (e.g., '2+ years'). Null if not mentioned."
    },
    "skills": {
      "type": ["array","null"],
      "items": { "type": "string" },
      "description": "Array of skills required for the job (e.g., ['JavaScript', 'ReactJS']). Null if not mentioned."
    },
    "timings": {
      "type": ["string","null"],
      "description": "Work timings if specified (e.g., '11AM - 8PM'). Null if not mentioned."
    },
    "email_subject": {
      "type": "string",
      "description": "The subject line of the email. Must always be present."
    },
    "email_body_chunks": {
      "type": "object",
      "properties": {
        "tldr": { "type": "string", "description": "Short summary of applicant focus/passion." },
        "para1": { "type": "string", "description": "Introduction paragraph expressing interest in the role." },
        "para2": { "type": "string", "description": "Skills, experience, and project highlights." },
        "para3": { "type": "string", "description": "References to portfolio, GitHub, LinkedIn." },
        "closing": { "type": "string", "description": "Polite call-to-action and signature." }
      },
      "required": ["tldr","para1","para2","para3","closing"],
      "description": "Email body broken into logical chunks for easier formatting and integration."
    }
  },
  "required": ["email_subject", "email_body_chunks"],
  "description": "JSON schema representing the extracted job details and the generated application email. Job info fields can be null if missing, but email_subject and email_body_chunks must always be returned."
}
